        return new Source("{% extends 'base.html.twig' %}

{% block title %}Edit User{% endblock %}

{% block body %}
    <div class=\"container py-5\">
        <div class=\"row\">
            <div class=\"col-md-6 mx-auto\">
                <div class=\"card shadow\">
                    <div class=\"card-header bg-primary text-white\">
                        <h1 class=\"h4 mb-0\">Edit User Information</h1>
                    </div>
                    <div class=\"card-body\">
                        {{ form_start(form) }}
                        
                        {% for message in app.flashes('success') %}
                            <div class=\"alert alert-success\">{{ message }}</div>
                        {% endfor %}
                        
                        {% for message in app.flashes('error') %}
                            <div class=\"alert alert-danger\">{{ message }}</div>
                        {% endfor %}

                        <div class=\"mb-3\">
                            {{ form_label(form.status, 'Account Status', {'label_attr': {'class': 'form-label'}}) }}
                            {{ form_widget(form.status, {'attr': {'class': 'form-control'}}) }}
                            {{ form_errors(form.status) }}
                        </div>

                        <div class=\"mb-3\">
                            {{ form_label(form.role, 'User Role', {'label_attr': {'class': 'form-label'}}) }}
                            {{ form_widget(form.role, {'attr': {'class': 'form-control'}}) }}
                            {{ form_errors(form.role) }}
                        </div>

                        <div class=\"d-flex justify-content-between mt-4\">
                            <a href=\"{{ path('app_user_index') }}\" class=\"btn btn-secondary\">
                                <i class=\"fas fa-arrow-left me-2\"></i> Back to list
                            </a>
                            <button type=\"submit\" class=\"btn btn-primary\">
                                <i class=\"fas fa-save me-2\"></i> Save Changes
                            </button>
                        </div>
                        {{ form_end(form) }}
                    </div>
                </div>
            </div>
        </div>
    </div>
{% endblock %}

{% block stylesheets %}
    {{ parent() }}
    <style>
        .card {
            border-radius: 10px;
        }
        .card-header {
            border-radius: 10px 10px 0 0 !important;
        }
        .form-control {
            border-radius: 5px;
        }
        .btn {
            border-radius: 5px;
        }
    </style>
{% endblock %}", "user/edit.html.twig", "C:\\Users\\motez\\OneDrive\\Bureau\\template-master - Copie\\template-master - Copie\\templates\\user\\edit.html.twig");
